mapper.select:
SELECT COUNT(*)
FROM tb_booking
WHERE booking_room_num = #{bookingRoomNum}
  and (checkin_date <= #{checkOutDate} and checkout_date >= #{checkInDate})
  AND booking_num != #{bookingNum}

SELECT
    *
FROM
    tb_room r
WHERE
    r.room_type = #{roomType}
    and
    r.room_people >= #{roomPeople}
    AND NOT EXISTS (
        SELECT 1
        FROM tb_booking b
        WHERE b.booking_room_num = r.room_num
        AND (
            (b.checkin_date <= #{checkOutDate} AND b.checkout_date >= #{checkInDate})
        )
    );
